params.src:
// Step 1: Capture the input value from the textbox (the message)
const inputValue = textInput1.value;

// Step 2: Set the messenger, which could be 'bot' or 'user'
const messenger = "user";  // Adjust this as needed, depending on who is sending

// Step 3: Get the current date and time
const currentDate = new Date();
const messageDate = currentDate.toISOString().split('T')[0];  // Format date as YYYY-MM-DD
const messageTime = currentDate.toTimeString().split(' ')[0]; // Format time as HH:MM:SS

// Function to generate a chatId based on the day and the first message
function generateChatId(firstMessage) {
  if (!firstMessage || typeof firstMessage !== "string") {
    return "InvalidMessage";  // Fallback chatId if something goes wrong
  }

  const currentDate = new Date();
  const formattedDate = currentDate.toISOString().split('T')[0].replace(/-/g, ''); // 'YYYYMMDD'
  const messageSnippet = firstMessage.replace(/\s+/g, '').substring(0, 10); // Remove spaces and take first 10 chars
  const chatId = `${formattedDate}-${messageSnippet}`;
  return chatId;
}

// Step 4: Check if the user selected 'New Chat' or an existing chat from the dropdown
const selectedChatId = listbox1.value; // Assuming this dropdown contains "New Chat" or chat IDs
let finalChatId;

// Step 5: If 'New Chat' is selected, generate a new chatId
if (selectedChatId === "New Chat") {
  finalChatId = generateChatId(inputValue);  // Generate new chatId using the first message
} else {
  finalChatId = selectedChatId;  // Use the selected existing chatId
}

// Step 6: First, trigger the AI query (query6) to get the AI's response
query6.trigger({
  onSuccess: function() {
    // Step 7: Get the AI response from query6.data
    const aiMessage = query6.data;  // Get the AI response directly from query6.data

    // Ensure the AI response is not null or empty
    if (aiMessage && aiMessage.trim() !== "") {
      // Step 8: Save the user message in the database using query3
      query3.trigger({
        additionalScope: {
          chatId: finalChatId,          // Use the generated or selected chatId
          valueToInsert: inputValue,    // Message content from the textbox
          valueMessenger: messenger,    // Either 'user' or 'bot'
          valueDate: messageDate,       // Current date
          valueTime: messageTime        // Current time
        },
        onSuccess: function(data) {
          utils.showNotification({
            title: "Message saved!",
            description: "User's message has been successfully saved to the database.",
            type: "success"
          });

          // Step 9: Save the AI's message as a new entry in the database
          query3.trigger({
            additionalScope: {
              chatId: finalChatId,          // Use the same chatId
              valueToInsert: aiMessage,     // AI response message
              valueMessenger: "bot",        // Mark it as coming from the "bot"
              valueDate: messageDate,       // Current date
              valueTime: messageTime        // Current time
            },
            onSuccess: function(data) {
              utils.showNotification({
                title: "AI Response Saved!",
                description: "The AI response has been successfully saved to the database.",
                type: "success"
              });
            },
            onFailure: function(error) {
              utils.showNotification({
                title: "Error",
                description: "Failed to save the AI response.",
                type: "error"
              });
            }
          });
        },
        onFailure: function(error) {
          utils.showNotification({
            title: "Error",
            description: "Failed to save the user message.",
            type: "error"
          });
        }
      });
    } else {
      // If AI didn't return a valid response
      utils.showNotification({
        title: "No Response from AI",
        description: "The AI did not return a valid response.",
        type: "warning"
      });
    }
  },
  onFailure: function(error) {
    utils.showNotification({
      title: "Error",
      description: "Failed to trigger AI query.",
      type: "error"
    });
  }
});

